mapper.update:
UPDATE group_member


        group_id=#{groupId},


        subject_id=#{subjectId},


        question_id=#{questionId},




        AND `user_id` = #{userId}


        AND `member_id` = #{groupMemberId}


        AND `eff_time` >= #{effTime}

update classroom_part
    group_id = #{groupId}
where
    classroom_id = #{classroomId}
  and userId = #{userId}
